import{_ as i,c as o,a4 as a,o as e}from"./chunks/framework.BvVMNBiX.js";const d=JSON.parse('{"title":"原生小程序","description":"","frontmatter":{},"headers":[],"relativePath":"miniprogram/mini/originMini.md","filePath":"miniprogram/mini/originMini.md"}'),t={name:"miniprogram/mini/originMini.md"};function c(n,l,p,r,u,s){return e(),o("div",null,l[0]||(l[0]=[a('<h1 id="原生小程序" tabindex="-1">原生小程序 <a class="header-anchor" href="#原生小程序" aria-label="Permalink to &quot;原生小程序&quot;">​</a></h1><h2 id="原生小程序发布上线" tabindex="-1">原生小程序发布上线 <a class="header-anchor" href="#原生小程序发布上线" aria-label="Permalink to &quot;原生小程序发布上线&quot;">​</a></h2><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p></p><h4 id="_1-发布" tabindex="-1">1. 发布 <a class="header-anchor" href="#_1-发布" aria-label="Permalink to &quot;1. 发布&quot;">​</a></h4><ol><li>前提：</li></ol><blockquote><ol><li>不能测试号上传发布</li><li>项目体积不能超过 2M</li></ol><blockquote><ul><li>图片全部都使用线上</li><li>tabBar图片只能使用本地图片</li></ul></blockquote></blockquote><ol start="2"><li>步骤</li></ol><blockquote><ol><li>使用uniapp构建打包项目</li><li>在小程序工具中导入要发布的项目（大小不能超过2M）</li><li>在小程序工具中点击 “上传”</li><li>回到微信公众平台上手动点击提交审核和上线</li></ol></blockquote><p> </p><h4 id="_2-分包" tabindex="-1">2. 分包 <a class="header-anchor" href="#_2-分包" aria-label="Permalink to &quot;2. 分包&quot;">​</a></h4><ol><li><p>定义：分包是小程序中用来解决项目体积过大和提高启动效率的一种技术</p></li><li><p>作用：</p></li></ol><blockquote><ol><li>解决小程序项目体积 2M大小限制 （20M）</li><li>提高页面启动效率</li></ol></blockquote><ol start="3"><li>使用：</li></ol><blockquote><ol><li>普通分包：</li></ol><blockquote><ol><li>packageA目录 理解为分包，其他范围为主包</li><li>在app.json文件中，通过关键字 subpackages 来配置分包</li></ol></blockquote><ol start="2"><li>独立分包：</li></ol><blockquote><ol><li>定义：独立分包是小程序中一种特殊类型的分包，能独立于主包和其他分包运行。从独立分包中页面进入小程序时，不需要下载主包。当用户进入普通分包或主包内页面时，主包才会被下载</li><li>作用：提高页面启动速度</li><li>使用：通过关键字 independent 来设置独立分包</li><li>注意：</li></ol><blockquote><ul><li>由于独立分包不依赖于主包，因此，不能使用主包或者分包中资源、全局样式、组件</li><li>一般都在创建项目的时候就考虑到用不用分包！！</li></ul></blockquote></blockquote><ol start="3"><li><p>分包预下载</p></li><li><p>分包异步化</p></li></ol></blockquote></div><p> </p><h2 id="原生微信小程序使用npm" tabindex="-1">原生微信小程序使用npm <a class="header-anchor" href="#原生微信小程序使用npm" aria-label="Permalink to &quot;原生微信小程序使用npm&quot;">​</a></h2><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p></p><ul><li>步骤：</li></ul><blockquote><ol><li>在根目录中初始化项目：<code>npm init</code></li><li>安装依赖 npm i 工具包</li><li>在微信开发者工具中，点击：工具 → 构建npm</li><li>代码中使用 dayjs：<code>import dayjs from &#39;dayjs&#39;</code></li></ol></blockquote></div><p> </p><h2 id="npm安装weui库" tabindex="-1">npm安装weui库 <a class="header-anchor" href="#npm安装weui库" aria-label="Permalink to &quot;npm安装weui库&quot;">​</a></h2><div class="tip custom-block github-alert"><p class="custom-block-title">TIP</p><p></p><ol><li><p>介绍：一套针对小程序的ui框架</p></li><li><p>步骤：</p></li></ol><blockquote><ol><li>初始化项目：<code>npm init -y</code></li><li>安装依赖：<code>npm i weui-miniprogram</code></li><li>构建npm：微信开发者工具 → 工具 → 构建npm</li><li>引入组件和样式</li><li>使用组件</li></ol></blockquote></div>',9)]))}const b=i(t,[["render",c]]);export{d as __pageData,b as default};
