import{_ as r,c as o,a2 as d,o as l}from"./chunks/framework.D3LXhGAZ.js";const b=JSON.parse('{"title":"Linux 命令","description":"","frontmatter":{},"headers":[],"relativePath":"sidebar/linux/linux/baseOrder.md","filePath":"sidebar/linux/linux/baseOrder.md"}'),a={name:"sidebar/linux/linux/baseOrder.md"};function h(c,t,e,i,g,n){return l(),o("div",null,t[0]||(t[0]=[d('<h1 id="linux-命令" tabindex="-1">Linux 命令 <a class="header-anchor" href="#linux-命令" aria-label="Permalink to &quot;Linux 命令&quot;">​</a></h1><h2 id="基础命令" tabindex="-1">基础命令 <a class="header-anchor" href="#基础命令" aria-label="Permalink to &quot;基础命令&quot;">​</a></h2><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>ip addr</td><td>查看IP地址</td></tr><tr style="background-color:var(--highlight-color);"><td>clear</td><td>清除屏幕</td></tr><tr style="background-color:var(--highlight-color);"><td>Ctrl + c</td><td>中断shell命令</td></tr><tr><th colspan="2">关闭系统</th></tr><tr style="background-color:var(--highlight-color);"><td>systemctl stop firewalld</td><td>关闭防火墙</td></tr><tr style="background-color:var(--highlight-color);"><td>shutdown -h now</td><td>立即关闭系统 /halt 关闭系统</td></tr><tr style="background-color:var(--highlight-color);"><td>shutdown -h 2</td><td>表示2分钟后关机</td></tr><tr style="background-color:var(--highlight-color);"><td>poweroff</td><td>立即关机</td></tr><tr><th colspan="2">重启系统</th></tr><tr style="background-color:var(--highlight-color);"><td>shutdown -r now</td><td>立即重启 / reboot 重启</td></tr><tr style="background-color:var(--highlight-color);"><td>shutdown -r 2</td><td>表示2分钟后重启</td></tr><tr style="background-color:var(--highlight-color);"><td>reboot</td><td>立即重启</td></tr></tbody></table><p> </p><h2 id="网络基础命令" tabindex="-1">网络基础命令 <a class="header-anchor" href="#网络基础命令" aria-label="Permalink to &quot;网络基础命令&quot;">​</a></h2><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>ifconfig</td><td>查看网卡信息</td></tr><tr style="background-color:var(--highlight-color);"><td>ping</td><td>查看网络的连接状况 比如：ping 127.0.0.1 检查网卡是否是坏的</td></tr><tr style="background-color:var(--highlight-color);"><td>netstat -ano</td><td>查看电脑的端口占用情况</td></tr><tr style="background-color:var(--highlight-color);"><td>tracert</td><td>检查网络的跃点（从A地到B地，需要经过哪些Ip地址）</td></tr></tbody></table><p> </p><h2 id="目录与文件命令" tabindex="-1">目录与文件命令 <a class="header-anchor" href="#目录与文件命令" aria-label="Permalink to &quot;目录与文件命令&quot;">​</a></h2><h3 id="_1-打印当前目录" tabindex="-1">1. 打印当前目录 <a class="header-anchor" href="#_1-打印当前目录" aria-label="Permalink to &quot;1. 打印当前目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>pwd</td><td>打印当前目录</td></tr></tbody></table><p> </p><h3 id="_2-查看目录" tabindex="-1">2. 查看目录 <a class="header-anchor" href="#_2-查看目录" aria-label="Permalink to &quot;2. 查看目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>ll 或者 ls -l</td><td>列表查看当前目录下的所有目录和文件（列表查看，显示更多信息）</td></tr><tr style="background-color:var(--highlight-color);"><td>ls -a</td><td>查看当前目录下的所有目录和文件（包括隐藏的文件）</td></tr><tr style="background-color:var(--highlight-color);"><td>ls /dir</td><td>查看指定目录下的所有目录和文件 如：ls /usr</td></tr></tbody></table><p> </p><h3 id="_3-切换目录" tabindex="-1">3. 切换目录 <a class="header-anchor" href="#_3-切换目录" aria-label="Permalink to &quot;3. 切换目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>cd /home</td><td>进入根目录下面的home目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd home</td><td>进入当前目录下的home目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd ..</td><td>返回上一层目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd ../..</td><td>返回上两级目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd /</td><td>返回根目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd -</td><td>返回上次所在的目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cd ~</td><td>切换到当前用户的主目录</td></tr></tbody></table><p> </p><h3 id="_4-创建目录" tabindex="-1">4. 创建目录 <a class="header-anchor" href="#_4-创建目录" aria-label="Permalink to &quot;4. 创建目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>mkdir dir1</td><td>创建一个叫做 &#39;dir1&#39; 的目录</td></tr><tr style="background-color:var(--highlight-color);"><td>mkdir dir1 dir2</td><td>同时创建两个目录</td></tr><tr style="background-color:var(--highlight-color);"><td>mkdir /usr/dir3</td><td>在指定目录下创建一个名为dir3的目录</td></tr></tbody></table><p> </p><h3 id="_5-创建文件" tabindex="-1">5. 创建文件 <a class="header-anchor" href="#_5-创建文件" aria-label="Permalink to &quot;5. 创建文件&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>touch filename</td><td>在当前目录创建一个名为xxx的文件</td></tr></tbody></table><p> </p><h3 id="_6-删除文件或目录" tabindex="-1">6. 删除文件或目录 <a class="header-anchor" href="#_6-删除文件或目录" aria-label="Permalink to &quot;6. 删除文件或目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">删除文件</td></tr><tr style="background-color:var(--highlight-color);"><td>rm filename</td><td>删除文件，它不会将文件移到回收站，而是直接删除</td></tr><tr style="background-color:var(--highlight-color);"><td>rm f1 f2 f3</td><td>删除多个文件</td></tr><tr style="background-color:var(--highlight-color);"><td>rm -f filename</td><td>强制删除文件（不询问确认）</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">删除目录</td></tr><tr style="background-color:var(--highlight-color);"><td>rm -r dir_name</td><td>删除目录及其内容，如果目录中有文件或子目录，rm 需要使用 -r（递归）选项来删除目录及其内容</td></tr><tr style="background-color:var(--highlight-color);"><td>rm -rf dir_name</td><td>强制删除目录及其内容，使用 -rf 选项，可以在没有任何确认的情况下强制递归删除目录及其所有内容</td></tr><tr style="background-color:var(--highlight-color);"><td>rm -rf /path/to/destination/</td><td>强制删除/path/to下的/destination/目录及其内容</td></tr><tr style="background-color:var(--highlight-color);"><td>rmdir empty_dir</td><td>当目录为空时，才能被删除；如果你不知道目录是否为空，可以使用 rm -r 删除目录</td></tr><tr style="background-color:var(--highlight-color);"><td>sudo rm -rf /*</td><td>这个命令会删除从根目录开始的所有文件和目录，包括系统文件、程序文件、配置文件、用户文件等。由于使用了 sudo，它会以 root 用户的身份执行，因此没有权限限制，会删除系统中的一切内容</td></tr></tbody></table><p> </p><h3 id="_7-移动文件或目录" tabindex="-1">7. 移动文件或目录 <a class="header-anchor" href="#_7-移动文件或目录" aria-label="Permalink to &quot;7. 移动文件或目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">文件</td></tr><tr style="background-color:var(--highlight-color);"><td>mv file.txt /path/to/destination/</td><td>将 file.txt 文件从当前目录移动到 /path/to/destination/ 目录</td></tr><tr style="background-color:var(--highlight-color);"><td>mv file1.txt file2.txt /path/to/destination/</td><td>将 file1.txt 和 file2.txt 文件移动到指定的目标目录</td></tr><tr style="background-color:var(--highlight-color);"><td>mv oldname.txt newname.txt</td><td>这个命令将 oldname.txt 文件重命名为 newname.txt</td></tr><tr style="background-color:var(--highlight-color);"><td>mv -i file.txt /path/to/destination/</td><td>如果目标目录中已存在同名文件，-i 选项会提示你是否覆盖文件</td></tr><tr style="background-color:var(--highlight-color);"><td>mv -n file.txt /path/to/destination/</td><td>-n 选项用于禁止覆盖任何已存在的文件，即使目标位置已有同名文件，mv 也不会覆盖它</td></tr><tr style="background-color:var(--highlight-color);"><td>mv -f file.txt /path/to/destination/</td><td>使用 -f 选项强制覆盖，不会给出确认提示</td></tr><tr style="background-color:var(--highlight-color);"><td>mv oldname.txt /path/to/destination/newname.txt</td><td>将文件 oldname.txt 移动到目标目录 /path/to/destination/，并将其重命名为 newname.txt</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">目录</td></tr><tr style="background-color:var(--highlight-color);"><td>mv dir1/ /path/to/destination/</td><td>将目录 dir1/ 从当前目录移动到 /path/to/destination/</td></tr><tr style="background-color:var(--highlight-color);"><td>mv olddir/ newdir/</td><td>这个命令将目录 olddir/ 重命名为 newdir/</td></tr></tbody></table><p> </p><h3 id="_8-查找目录或文件" tabindex="-1">8. 查找目录或文件 <a class="header-anchor" href="#_8-查找目录或文件" aria-label="Permalink to &quot;8. 查找目录或文件&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;file.txt&quot;</td><td>查找当前目录及子目录下名为 file.txt 的文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -iname &quot;file.txt&quot;</td><td>忽略大小写查找文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -type f</td><td>查找当前目录及其子目录下的所有文件（不包括目录）；可以将 . 替换成具体路径表示特定目录下查找</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -size +10M</td><td>查找大于 10MB 的文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -size -100k</td><td>查找小于 100KB 的文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -mtime -7</td><td>查找最近 7 天内修改过的文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -mtime +30</td><td>查找 30 天前修改的文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;*.log&quot; -exec rm -f {} \\;</td><td>查找并删除所有 .log 文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;*.sh&quot; -exec chmod +x {} \\;</td><td>查找并更改文件权限（例如，给所有 .sh 文件加执行权限）</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;*.txt&quot;</td><td>只显示匹配的文件路径（默认行为）</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;*.txt&quot; -exec ls -l {} \\;</td><td>查找并显示详细信息（类似 ls -l）</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -name &quot;*.txt&quot; -size +1M -mtime -10</td><td>查找大于 1MB 且在最近 10天内修改的 .txt 文件</td></tr><tr style="background-color:var(--highlight-color);"><td>find . \\( -name &quot;*.txt&quot; -o -name &quot;*.log&quot; \\)</td><td>查找多个条件（例如同时匹配 .txt 和 .log 文件）</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">目录</td></tr><tr style="background-color:var(--highlight-color);"><td>find . -type d</td><td>查找所有目录</td></tr></tbody></table><p> </p><h3 id="_9-复制文件和目录" tabindex="-1">9. 复制文件和目录 <a class="header-anchor" href="#_9-复制文件和目录" aria-label="Permalink to &quot;9. 复制文件和目录&quot;">​</a></h3><table><thead><tr><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">cp [选项] 源路径 目标路径</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">文件</td></tr><tr style="background-color:var(--highlight-color);"><td>cp file.txt /path/to/destination/</td><td>将文件 file.txt 复制到 /path/to/destination/ 目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cp file1.txt file2.txt /path/to/destination/</td><td>将 file1.txt 和 file2.txt 复制到 /path/to/destination/</td></tr><tr style="background-color:var(--highlight-color);"><td>cp file.txt /path/to/destination/newfile.txt</td><td>将 file.txt 复制到 /path/to/destination/ 并重命名为 newfile.txt</td></tr><tr style="background-color:var(--highlight-color);"><td>cp -f file.txt /path/to/destination/</td><td>如果目标文件已经存在，cp 默认会覆盖它。如果不希望被提示，使用 -f（force）选项强制复制并覆盖目标文件</td></tr><tr style="background-color:var(--highlight-color);"><td>cp -p file.txt /path/to/destination/</td><td>使用 -p 选项可以保留文件的时间戳、权限、所有者等属性</td></tr><tr style="background-color:var(--highlight-color);"><td>cp -i file.txt /path/to/destination/</td><td>使用 -i（interactive）选项，在目标文件已存在时，cp 会提示用户确认是否覆盖</td></tr><tr style="background-color:var(--highlight-color);"><td>cp -P symlink /path/to/destination/</td><td>如果要复制符号链接（symlink），而不是复制符号链接指向的文件，可以使用 -P 选项</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">目录</td></tr><tr style="background-color:var(--highlight-color);"><td>cp -r dir1/ /path/to/destination/</td><td>将目录 dir1/ 复制到 /path/to/destination/</td></tr></tbody></table><p> </p><h2 id="压缩与解压" tabindex="-1">压缩与解压 <a class="header-anchor" href="#压缩与解压" aria-label="Permalink to &quot;压缩与解压&quot;">​</a></h2><h3 id="_1-tar" tabindex="-1">1. tar <a class="header-anchor" href="#_1-tar" aria-label="Permalink to &quot;1. tar&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">tar (用于 .tar, .tar.gz, .tar.bz2, .tar.xz 等格式)</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -xvf archive.tar</td><td>解压 .tar 文件；-x：解压归档文件</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -xzvf archive.tar.gz</td><td>解压 .tar.gz 文件；-z：解压 .tar.gz 格式的压缩文件</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -xjvf archive.tar.bz2</td><td>解压 .tar.bz2 文件；-j：解压 .tar.bz2 格式的压缩文件</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -xJvf archive.tar.xz</td><td>解压 .tar.xz 文件；-J：解压 .tar.xz 格式的压缩文件</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -cvf archive.tar file1 file2 dir1</td><td>打包文件（创建 .tar 文件）</td></tr><tr style="background-color:var(--highlight-color);"><td>tar -czvf archive.tar.gz file1 file2 dir1</td><td>-z：通过 gzip 压缩归档文件（生成 .tar.gz 文件）</td></tr></tbody></table><p> </p><h3 id="_2-gzip" tabindex="-1">2. gzip <a class="header-anchor" href="#_2-gzip" aria-label="Permalink to &quot;2. gzip&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">gzip 是用于压缩文件的工具，通常生成 .gz 文件</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>gzip -d file.txt.gz 或者 gunzip file.txt.gz</td><td>解压 .gz 文件</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>gzip file.txt</td><td>将 file.txt 压缩为 file.txt.gz</td></tr><tr style="background-color:var(--highlight-color);"><td>gzip -k file.txt</td><td>-k 选项表示压缩后保留原文件</td></tr></tbody></table><p> </p><h3 id="_3-bzip2" tabindex="-1">3. bzip2 <a class="header-anchor" href="#_3-bzip2" aria-label="Permalink to &quot;3. bzip2&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">bzip2 (用于 .bz2 格式)</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>bzip2 -d file.txt.bz2 或者 bunzip2 file.txt.bz2</td><td>这两种方式都能解压 file.txt.bz2</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>bzip2 file.txt</td><td>将 file.txt 压缩为 file.txt.bz2</td></tr><tr style="background-color:var(--highlight-color);"><td>bzip2 -k file.txt</td><td>-k 选项表示压缩后保留原文件</td></tr></tbody></table><p> </p><h3 id="_4-zip" tabindex="-1">4. zip <a class="header-anchor" href="#_4-zip" aria-label="Permalink to &quot;4. zip&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">zip (用于 .zip 格式)</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>unzip archive.zip</td><td>解压 archive.zip 文件</td></tr><tr style="background-color:var(--highlight-color);"><td>unzip -l archive.zip</td><td>只查看 .zip 文件内容</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>zip archive.zip file1.txt file2.txt dir1</td><td>将 file1.txt、file2.txt 和目录 dir1 压缩为 archive.zip 文件</td></tr></tbody></table><p> </p><h3 id="_5-7z" tabindex="-1">5. 7z <a class="header-anchor" href="#_5-7z" aria-label="Permalink to &quot;5. 7z&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">7z (用于 .7z 格式)</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>7z x archive.7z</td><td>解压 .7z 文件；x：解压文件</td></tr><tr style="background-color:var(--highlight-color);"><td>7z l archive.7z</td><td>查看 .7z 文件内容</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>7z a archive.7z file1.txt file2.txt dir1</td><td>压缩文件；a：添加文件到归档</td></tr></tbody></table><p> </p><h3 id="_6-xz" tabindex="-1">6. xz <a class="header-anchor" href="#_6-xz" aria-label="Permalink to &quot;6. xz&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">xz (用于 .xz 格式)</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">解压</td></tr><tr style="background-color:var(--highlight-color);"><td>xz -d file.txt.xz 或者 unxz file.txt.xz</td><td>解压 .xz 文件</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">压缩</td></tr><tr style="background-color:var(--highlight-color);"><td>xz file.txt</td><td>将 file.txt 压缩为 file.txt.xz</td></tr></tbody></table><p> </p><h2 id="cat命令" tabindex="-1">cat命令 <a class="header-anchor" href="#cat命令" aria-label="Permalink to &quot;cat命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">cat [选项] 文件...</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">用于查看文件内容、合并文件内容、以及创建文件的常用 Linux 命令</td></tr><tr style="background-color:var(--highlight-color);"><td>cat file.txt</td><td>查看 file.txt 文件的内容</td></tr><tr style="background-color:var(--highlight-color);"><td>cat file1.txt file2.txt</td><td>一次性查看多个文件的内容，cat 会按顺序显示所有文件的内容</td></tr><tr style="background-color:var(--highlight-color);"><td>cat file1.txt file2.txt &gt; combined.txt</td><td>将 file1.txt 和 file2.txt 的内容合并，并保存到 combined.txt 文件中。如果 combined.txt 文件已经存在，它会被覆盖</td></tr><tr style="background-color:var(--highlight-color);"><td>cat file1.txt &gt;&gt; file2.txt</td><td>将 file1.txt 的内容追加到 file2.txt 文件的末尾，而不是覆盖它</td></tr><tr style="background-color:var(--highlight-color);"><td>cat -n file.txt</td><td>输出 file.txt 的内容，并为每一行显示行号</td></tr><tr style="background-color:var(--highlight-color);"><td>cat -E file.txt</td><td>在每行的末尾显示一个 $ 符号</td></tr><tr style="background-color:var(--highlight-color);"><td>cat -T file.txt</td><td>显示文件中的制表符字符（如果有的话），并将它们显示为 ^I</td></tr><tr><td>cat -v file.txt</td><td>它会以可见的方式显示非打印字符</td></tr><tr style="background-color:var(--highlight-color);"><td>cat &gt; newfile.txt</td><td>让你输入文本，当你按下 Ctrl+D 结束输入时，内容会被写入到 newfile.txt 文件中</td></tr><tr style="background-color:var(--highlight-color);"><td>cat -s file1.txt file2.txt</td><td>有时候你可能希望在多个文件之间添加分隔符。可以使用 -s 选项来在连续的空行之间压缩为一行</td></tr></tbody></table><p> </p><h2 id="wc命令" tabindex="-1">wc命令 <a class="header-anchor" href="#wc命令" aria-label="Permalink to &quot;wc命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">wc [选项] [文件名]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于统计文件中字符数、单词数和行数的命令</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>wc -l example.txt</td><td>只统计行数；输出：10 example.txt</td></tr><tr style="background-color:var(--highlight-color);"><td>-l</td><td>只显示行数（lines）</td></tr><tr style="background-color:var(--highlight-color);"><td>-w</td><td>只显示单词数（words）</td></tr><tr style="background-color:var(--highlight-color);"><td>-c</td><td>只显示字节数（bytes）</td></tr><tr style="background-color:var(--highlight-color);"><td>-m</td><td>只显示字符数（characters）。注意，这与 -c 不同，-m 统计的是字符数量，支持多字节字符</td></tr><tr style="background-color:var(--highlight-color);"><td>-L</td><td>显示最长行的长度（longest line length）</td></tr></tbody></table><p> </p><h2 id="which命令" tabindex="-1">which命令 <a class="header-anchor" href="#which命令" aria-label="Permalink to &quot;which命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">which [选项] 命令名</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">查找命令的可执行文件路径；验证命令是否安装或可用；显示命令所在的位置</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>which ls</td><td>查找单个命令的位置；输出：/bin/ls</td></tr><tr style="background-color:var(--highlight-color);"><td>which ls grep</td><td>查找多个命令的位置</td></tr><tr style="background-color:var(--highlight-color);"><td>which -a python</td><td>显示所有匹配的路径</td></tr></tbody></table><p> </p><h2 id="grep命令" tabindex="-1">grep命令 <a class="header-anchor" href="#grep命令" aria-label="Permalink to &quot;grep命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">grep [选项] 模式 [文件...]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于在文件中搜索指定的模式（字符串或正则表达式）</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>grep &quot;hello&quot; example.txt</td><td>输出文件 example.txt 中包含 hello 的行</td></tr><tr style="background-color:var(--highlight-color);"><td>grep -i &quot;hello&quot; example.txt</td><td>忽略大小写搜索</td></tr><tr style="background-color:var(--highlight-color);"><td>grep &quot;error&quot; file1.txt file2.txt</td><td>在多个文件中搜索</td></tr><tr style="background-color:var(--highlight-color);"><td>grep -r &quot;TODO&quot; /path/to/code</td><td>递归搜索目录</td></tr><tr style="background-color:var(--highlight-color);"><td>grep -n &quot;main&quot; example.c</td><td>显示匹配行的行号</td></tr><tr style="background-color:var(--highlight-color);"><td>grep -l &quot;password&quot; *.txt</td><td>只显示包含匹配模式的文件名</td></tr><tr style="background-color:var(--highlight-color);"><td>grep &quot;^Error&quot; log.txt</td><td>匹配以 Error 开头的行</td></tr><tr style="background-color:var(--highlight-color);"><td>grep &quot;[0-9]$&quot; data.txt</td><td>匹配以数字结尾的行</td></tr><tr style="background-color:var(--highlight-color);"><td>dmesg | grep &quot;error&quot;</td><td>从命令输出中过滤结果</td></tr><tr style="background-color:var(--highlight-color);"><td>find /path/to/dir -name &quot;*.log&quot; -exec grep &quot;error&quot; {} +</td><td>结合 find 和 grep</td></tr></tbody></table><p> </p><h2 id="chmod命令" tabindex="-1">chmod命令 <a class="header-anchor" href="#chmod命令" aria-label="Permalink to &quot;chmod命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">chmod [选项] 模式 文件名</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于更改文件或目录的权限；权限分为三种：读（r）、写（w） 和 执行（x），分别针对三类用户：所有者（owner）、组（group） 和 其他人（others）</td></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">1. 权限的数字表示法</td></tr><tr style="background-color:var(--highlight-color);"><td>读 (r) = 4；<br>写 (w) = 2；<br>执行 (x) = 1；<br>无权限 (-) = 0</td><td>文件权限由 3 位八进制数字组成，每位分别表示所有者、组和其他人的权限，每种权限用数字表示</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod 755 file</td><td>所有者：rw-，组：r--，其他人：r--</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod 644 file.txt</td><td>所有者有 rwx，组和其他人有 r-x</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">2. 符号表示法</td></tr><tr style="background-color:var(--highlight-color);"><td>u：所有者（user）；<br>g：组（group）；<br>o：其他人（others）；<br>a：所有用户（all，等同于 ugo）</td><td>用户类别</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod u+x file</td><td>为所有者添加执行权限</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod g-w file</td><td>移除组的写权限</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod o=r file</td><td>设置其他人为只读权限</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod -R 755 /path/to/directory</td><td>递归修改目录权限</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod --reference=reference.txt file.txt</td><td>设置与参考文件一致的权限</td></tr><tr style="background-color:var(--highlight-color);"><td>chmod o= file.txt</td><td>移除其他人的所有权限</td></tr></tbody></table><p> </p><h2 id="用户管理" tabindex="-1">用户管理 <a class="header-anchor" href="#用户管理" aria-label="Permalink to &quot;用户管理&quot;">​</a></h2><h3 id="_1-创建用户" tabindex="-1">1. 创建用户 <a class="header-anchor" href="#_1-创建用户" aria-label="Permalink to &quot;1. 创建用户&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">useradd [选项] 用户名</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>-m</td><td>为用户创建主目录（通常是 /home/用户名）</td></tr><tr style="background-color:var(--highlight-color);"><td>-d [目录]</td><td>指定主目录路径</td></tr><tr style="background-color:var(--highlight-color);"><td>-s [shell]</td><td>指定用户登录后使用的默认 shell（如 /bin/bash）</td></tr><tr style="background-color:var(--highlight-color);"><td>-g [组名]</td><td>指定用户的初始组</td></tr><tr style="background-color:var(--highlight-color);"><td>-G [组名]</td><td>指定用户的附加组</td></tr><tr style="background-color:var(--highlight-color);"><td>-u [UID]</td><td>指定用户的 UID</td></tr><tr style="background-color:var(--highlight-color);"><td>-c [描述]</td><td>添加用户描述信息</td></tr><tr style="background-color:var(--highlight-color);"><td>useradd -m -s /bin/bash -c &quot;开发人员&quot; devuser</td><td></td></tr></tbody></table><p> </p><h3 id="_2-修改用户" tabindex="-1">2. 修改用户 <a class="header-anchor" href="#_2-修改用户" aria-label="Permalink to &quot;2. 修改用户&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">usermod [选项] 用户名</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>-l [新用户名]</td><td>修改用户名</td></tr><tr style="background-color:var(--highlight-color);"><td>-d [新主目录] -m</td><td>移动并修改主目录</td></tr><tr style="background-color:var(--highlight-color);"><td>-s [shell]</td><td>修改默认 shell</td></tr><tr style="background-color:var(--highlight-color);"><td>-g [组名]</td><td>修改初始组</td></tr><tr style="background-color:var(--highlight-color);"><td>-G [组名1,组名2]</td><td>修改附加组</td></tr><tr style="background-color:var(--highlight-color);"><td>-L</td><td>锁定用户账户</td></tr><tr style="background-color:var(--highlight-color);"><td>-U</td><td>解锁用户账户</td></tr><tr style="background-color:var(--highlight-color);"><td>usermod -s /bin/zsh devuser</td><td></td></tr></tbody></table><p> </p><h3 id="_3-删除用户" tabindex="-1">3. 删除用户 <a class="header-anchor" href="#_3-删除用户" aria-label="Permalink to &quot;3. 删除用户&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th colspan="2">userdel [选项] 用户名</th></tr><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>-r 用户名</td><td>删除用户的同时，删除其主目录及相关文件</td></tr><tr style="background-color:var(--highlight-color);"><td>userdel -r devuser</td><td></td></tr></tbody></table><p> </p><h3 id="_4-查看用户信息" tabindex="-1">4. 查看用户信息 <a class="header-anchor" href="#_4-查看用户信息" aria-label="Permalink to &quot;4. 查看用户信息&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>whoami</td><td>查看当前用户</td></tr><tr style="background-color:var(--highlight-color);"><td>who</td><td>查看登录用户</td></tr><tr style="background-color:var(--highlight-color);"><td>cat /etc/passwd</td><td>查看所有系统用户</td></tr></tbody></table><p> </p><h3 id="_5-设置用户密码" tabindex="-1">5. 设置用户密码 <a class="header-anchor" href="#_5-设置用户密码" aria-label="Permalink to &quot;5. 设置用户密码&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>passwd</td><td>当前用户修改密码</td></tr><tr style="background-color:var(--highlight-color);"><td>passwd devuser</td><td>管理员为其他用户设置密码</td></tr></tbody></table><p> </p><h3 id="_6-用户组管理" tabindex="-1">6. 用户组管理 <a class="header-anchor" href="#_6-用户组管理" aria-label="Permalink to &quot;6. 用户组管理&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:#var(--dark-color);"><td colspan="2">1. 创建用户组</td></tr><tr style="background-color:var(--highlight-color);"><td>groupadd [组名]</td><td>创建用户组</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">2. 修改用户组</td></tr><tr style="background-color:var(--highlight-color);"><td>groupmod -n [新组名] [旧组名]</td><td>修改用户组</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">3. 删除用户组</td></tr><tr style="background-color:var(--highlight-color);"><td>groupdel [组名]</td><td>删除用户组</td></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">4. 将用户添加到组</td></tr><tr style="background-color:var(--highlight-color);"><td>usermod -aG [组名] [用户名]</td><td>将用户添加到组</td></tr></tbody></table><p> </p><h3 id="_7-切换用户" tabindex="-1">7. 切换用户 <a class="header-anchor" href="#_7-切换用户" aria-label="Permalink to &quot;7. 切换用户&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>su [用户名]</td><td>切换到另一个用户</td></tr><tr style="background-color:var(--highlight-color);"><td>sudo [命令]</td><td>需要当前用户在 sudoers 文件中具有权限</td></tr></tbody></table><p> </p><h2 id="系统-进程命令" tabindex="-1">系统/进程命令 <a class="header-anchor" href="#系统-进程命令" aria-label="Permalink to &quot;系统/进程命令&quot;">​</a></h2><h3 id="_1-kill" tabindex="-1">1. kill <a class="header-anchor" href="#_1-kill" aria-label="Permalink to &quot;1. kill&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">kill [选项] [PID]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">通常用来终止进程</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>kill [PID]</td><td>终止进程</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -9 [PID]</td><td>强制终止进程；发送 SIGKILL 信号，立即终止目标进程</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -s HUP [PID]</td><td>发送 SIGHUP 信号，通常用于重新加载服务配置</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -9 [PID1] [PID2] [PID3]</td><td>终止多个进程</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -18 [PID]</td><td>继续运行已暂停的进程</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -l</td><td>列出所有信号</td></tr><tr style="background-color:var(--highlight-color);"><td>ps aux | grep [进程名] <br> kill [PID]</td><td>使用 ps 或 pgrep 查找进程后用 kill 终止</td></tr><tr style="background-color:var(--highlight-color);"><td>pkill [进程名]</td><td>直接通过进程名终止</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -9 $(pgrep [进程名])</td><td>结合 pgrep 和 kill，通过进程名终止</td></tr><tr style="background-color:var(--highlight-color);"><td>kill -u [用户名]</td><td>终止某用户的所有进程</td></tr><tr style="background-color:var(--highlight-color);"><td colspan="2">权限问题：1.普通用户只能终止自己拥有的进程；2.需要使用 sudo 终止其他用户的进程或系统级进程</td></tr></tbody></table><p> </p><h3 id="_2-ps" tabindex="-1">2. ps <a class="header-anchor" href="#_2-ps" aria-label="Permalink to &quot;2. ps&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">ps [选项]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示当前运行中的进程信息。它可以显示进程的 PID、所属用户、运行状态、使用的资源等</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>ps aux</td><td>查看所有进程</td></tr><tr style="background-color:var(--highlight-color);"><td>ps -u root</td><td>查看特定用户的进程</td></tr><tr style="background-color:var(--highlight-color);"><td>ps -u root</td><td>查看特定用户的进程</td></tr><tr style="background-color:var(--highlight-color);"><td>ps aux | grep apache</td><td>结合 grep 查找特定进程</td></tr></tbody></table><p> </p><h3 id="_3-free" tabindex="-1">3. free <a class="header-anchor" href="#_3-free" aria-label="Permalink to &quot;3. free&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">free [选项]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">查看内存使用情况</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>free -m</td><td>显示以 MB 为单位的内存信息</td></tr></tbody></table><p> </p><h3 id="_4-du" tabindex="-1">4. du <a class="header-anchor" href="#_4-du" aria-label="Permalink to &quot;4. du&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">du [选项] [文件或目录]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用来统计目录及文件的空间占用情况 下面是常用的选项</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>du -h</td><td>以人类可读的格式显示（KB、MB、GB）</td></tr><tr style="background-color:var(--highlight-color);"><td>du -k</td><td>以 KB 为单位显示（默认单位）</td></tr><tr style="background-color:var(--highlight-color);"><td>du -m</td><td>以 MB 为单位显示</td></tr><tr style="background-color:var(--highlight-color);"><td>du -a</td><td>显示目录及其所有文件的磁盘使用情况</td></tr><tr style="background-color:var(--highlight-color);"><td>du -s</td><td>仅显示指定目录或文件的总计</td></tr><tr style="background-color:var(--highlight-color);"><td>du --max-depth=N</td><td>限制显示的目录深度</td></tr></tbody></table><p> </p><h3 id="_5-vmstat" tabindex="-1">5. vmstat <a class="header-anchor" href="#_5-vmstat" aria-label="Permalink to &quot;5. vmstat&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">vmstat [选项] [刷新间隔] [刷新次数]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示系统的 虚拟内存、进程、CPU、I/O 和 上下文切换 等统计信息</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>vmstat</td><td>显示一次当前的系统统计信息</td></tr><tr style="background-color:var(--highlight-color);"><td>vmstat 2</td><td>每 2 秒刷新一次，持续监控</td></tr><tr style="background-color:var(--highlight-color);"><td>vmstat 1 5</td><td>每 1 秒刷新一次，输出 5 次后退出</td></tr><tr style="background-color:var(--highlight-color);"><td>vmstat -m</td><td>以 MB 为单位显示内存统计</td></tr><tr style="background-color:var(--highlight-color);"><td>vmstat -d</td><td>显示磁盘统计信息</td></tr></tbody></table><p> </p><h3 id="_6-iostat" tabindex="-1">6. iostat <a class="header-anchor" href="#_6-iostat" aria-label="Permalink to &quot;6. iostat&quot;">​</a></h3><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">iostat [选项] [时间间隔] [显示次数]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示系统 I/O 设备（如磁盘）的性能统计信息的命令。它可以帮助你监控磁盘的读写性能，CPU 的使用情况</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>iostat -c</td><td>显示 CPU 使用情况</td></tr><tr style="background-color:var(--highlight-color);"><td>iostat -d</td><td>显示设备的 I/O 使用情况（磁盘读写、设备活动等）</td></tr><tr style="background-color:var(--highlight-color);"><td>iostat -x</td><td>显示扩展统计信息，包括更多磁盘 I/O 性能指标（如响应时间、利用率等）</td></tr><tr style="background-color:var(--highlight-color);"><td>iostat -t</td><td>显示时间戳</td></tr><tr style="background-color:var(--highlight-color);"><td>iostat -p sda</td><td>显示指定设备的统计信息</td></tr></tbody></table><p> </p><h2 id="type命令" tabindex="-1">type命令 <a class="header-anchor" href="#type命令" aria-label="Permalink to &quot;type命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">type [选项] 命令</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示命令类型和性质的 Linux 命令。它可以告诉你指定命令是内建命令、可执行文件、别名、关键字还是函数等</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>type -a 命令</td><td>显示所有可能的命令类型（包括内建命令、别名、可执行文件等）</td></tr><tr style="background-color:var(--highlight-color);"><td>type -t 命令</td><td>仅返回命令的类型，不显示详细信息</td></tr><tr style="background-color:var(--highlight-color);"><td>type -p 命令</td><td>显示命令的路径（如果是可执行文件的话）</td></tr><tr style="background-color:var(--highlight-color);"><td>type -f 命令</td><td>显示命令的完整路径（包括命令所在目录）</td></tr><tr style="background-color:var(--highlight-color);"><td>type ls</td><td>查看 ls 的类型</td></tr><tr style="background-color:var(--highlight-color);"><td>type echo</td><td>查看 echo 的类型（内建命令）</td></tr><tr style="background-color:var(--highlight-color);"><td>type ll</td><td>查看命令 ll 的类型</td></tr><tr style="background-color:var(--highlight-color);"><td>type -t cd</td><td>显示 cd 命令的类型</td></tr><tr style="background-color:var(--highlight-color);"><td>type -a grep</td><td>显示所有 grep 的类型</td></tr></tbody></table><p> </p><h2 id="history命令" tabindex="-1">history命令 <a class="header-anchor" href="#history命令" aria-label="Permalink to &quot;history命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">history [选项] [n]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示和管理 Linux 命令历史的命令。它可以帮助你查看之前执行过的命令、重复执行历史命令以及清理历史记录等</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>history -c</td><td>清空所有历史记录</td></tr><tr style="background-color:var(--highlight-color);"><td>history 10</td><td>查看最后 10 条历史记录</td></tr><tr style="background-color:var(--highlight-color);"><td>history -d 10</td><td>删除历史记录中的第 10 条命令</td></tr><tr style="background-color:var(--highlight-color);"><td>history -a</td><td>将当前会话中的历史命令追加到历史文件（~/.bash_history）中</td></tr><tr style="background-color:var(--highlight-color);"><td>history -r</td><td>从历史文件（~/.bash_history）读取历史命令</td></tr><tr style="background-color:var(--highlight-color);"><td>history -w</td><td>将当前会话中的历史命令写入到历史文件（~/.bash_history）中</td></tr><tr style="background-color:var(--highlight-color);"><td>history -p</td><td>根据模式替换当前的历史记录</td></tr><tr style="background-color:var(--highlight-color);"><td>history -n</td><td>读取未添加到历史文件中的命令</td></tr><tr style="background-color:var(--highlight-color);"><td>!10</td><td>执行编号为 10 的命令</td></tr><tr style="background-color:var(--highlight-color);"><td>!!</td><td>执行最后一条历史命令</td></tr><tr style="background-color:var(--highlight-color);"><td>!ls</td><td>查找并执行最近一个以 ls 开头的命令</td></tr><tr style="background-color:var(--highlight-color);"><td>cat ~/.bash_history</td><td>查看历史文件中的命令</td></tr></tbody></table><p> </p><h2 id="help命令" tabindex="-1">help命令 <a class="header-anchor" href="#help命令" aria-label="Permalink to &quot;help命令&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">help [命令]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示内置命令的帮助信息的命令。它通常用于获取 Bash 或其他 shell 环境中的内置命令的简要说明和用法</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>help cd</td><td>显示 cd 命令的帮助信息，包括用法、选项和示例</td></tr><tr style="background-color:var(--highlight-color);"><td>help</td><td>列出所有可用的内建命令，并显示每个命令的简要说明</td></tr><tr style="background-color:var(--highlight-color);"><td>help -d echo</td><td>查看特定内建命令的简要描述</td></tr><tr style="background-color:var(--highlight-color);"><td>help -a</td><td>查看所有内建命令的详细帮助信息</td></tr><tr style="background-color:var(--highlight-color);"><td>help -s test</td><td>查看内建命令的简单语法</td></tr><tr style="background-color:var(--highlight-color);"><td>help -v</td><td>查看帮助信息时的版本信息</td></tr></tbody></table><p> </p><h2 id="时间日期类" tabindex="-1">时间日期类 <a class="header-anchor" href="#时间日期类" aria-label="Permalink to &quot;时间日期类&quot;">​</a></h2><table><thead><tr style="background-color:#var(--dark-color);"><th>命令</th><th>说明</th></tr><tr style="background-color:#var(--dark-color);"><th colspan="2">date [选项] [+格式]</th></tr><tr style="background-color:#var(--dark-color);"><td colspan="2">用于显示和设置系统日期和时间的 Linux 命令。它可以显示当前的日期和时间，并支持多种格式化选项来修改输出的日期和时间格式</td></tr></thead><tbody><tr style="background-color:var(--highlight-color);"><td>date -u</td><td>查看当前 UTC 时间</td></tr><tr style="background-color:var(--highlight-color);"><td>date &quot;+%Y-%m-%d %H:%M:%S&quot;</td><td>自定义日期和时间格式</td></tr><tr style="background-color:var(--highlight-color);"><td>date -d &quot;2024-12-25 12:00:00&quot;</td><td>显示指定日期和时间</td></tr><tr style="background-color:var(--highlight-color);"><td>date -d &quot;next Monday&quot;</td><td>显示下一个星期一的日期</td></tr><tr style="background-color:var(--highlight-color);"><td>sudo date --set=&quot;2024-12-25 12:00:00&quot;</td><td>将当前时间设置为 2024 年 12 月 25 日 12:00:00</td></tr><tr style="background-color:var(--highlight-color);"><td>date -R</td><td>显示 RFC 2822 格式的日期</td></tr><tr style="background-color:var(--highlight-color);"><td>date -I</td><td>显示 ISO 8601 格式的日期</td></tr><tr style="background-color:var(--highlight-color);"><td>date -r myfile.txt</td><td>查看文件的最后修改时间</td></tr></tbody></table>',121)]))}const u=r(a,[["render",h]]);export{b as __pageData,u as default};
